// src/Pages/Admin/AdminLayout.js
import React, { useState, useEffect } from "react";
import { useNavigate, Outlet } from "react-router-dom";
import api from "../../axiosConfig";
import "./AdminDashboard.css"; // You can rename to AdminLayout.css if needed

const AdminLayout = () => {
  const navigate = useNavigate();
  const [adminName, setAdminName] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/UserLogin");
      return;
    }

    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      setAdminName(
        payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]
      );
    } catch (err) {
      console.error("Failed to parse token:", err);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {adminName}</h2>
        <nav className="dashboard-nav">
          <button onClick={() => navigate("/Admin/AllUsers")}>All Users</button>
          <button onClick={() => navigate("/Admin/PendingFeedbacks")}>
            Approve Pending Feedbacks
          </button>
          <button onClick={() => navigate("/Admin/ApproveFeedbackById")}>
            Approve by ID
          </button>
        </nav>
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </header>

      <main className="dashboard-content">
        {/* This is where each page's content will be rendered */}
        <Outlet />
      </main>
    </div>
  );
};

export default AdminLayout;
