import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
// 1. IMPORT YOUR LOGO
import AppLogo from '../assets/logo.png'; // â¬…ï¸ ADJUST PATH AS NEEDED

const SplashScreen = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Timer controls the duration of the splash screen
    const timer = setTimeout(() => {
      navigate("/UserLogin");
    }, 2500);

    // Cleanup function to prevent memory leaks if the component unmounts early
    return () => clearTimeout(timer);
  }, [navigate]); // Added 'navigate' to dependency array

  return (
    <div
      className="splash-container"
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#f8f9fa",
        fontFamily: "Arial",
      }}
    >
      <div
        className="splash-content"
        style={{ textAlign: "center" }}
      >
        {/* 2. ADD THE LOGO IMAGE */}
        <img 
          src={AppLogo} 
          alt="Rate Right Logo" 
          style={{
            width: '150px', // Adjust size for better visibility
            height: 'auto',
            marginBottom: '20px', // Spacing between logo and title
            animation: 'fadeIn 1s ease-out', // Add a simple entrance animation (CSS required)
          }}
        />
        
        <h1
          className="splash-title"
          style={{
            fontSize: "28px",
            fontWeight: "bold",
            color: "#333",
            marginBottom: "10px",
          }}
        >
          Welcome to RATE RIGHT ðŸŒŸ
        </h1>

        <p
          className="splash-subtitle"
          style={{
            fontSize: "16px",
            color: "#666",
          }}
        >
          Loading...
        </p>
      </div>
    </div>
  );
};

export default SplashScreen;
