import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [action, setAction] = useState('Login'); // Login or Register

  const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    let response;

    if (action === "Login") {
      // LOGIN API
      response = await axios.post("https://localhost:7079/api/UserLogin/login", {
        Username: username,
        Password: password,
      });

      localStorage.setItem("token", response.data.token);
      navigate("/userdashboard");
    } else {
      // REGISTER API
      response = await axios.post("https://localhost:7079/api/UserLogin/register", {
        Username: username,
        Password: password,
        name: fullName,
      });

      alert("Registered successfully! Please login now.");
      setAction("Login");
    }
  } catch (err) {
    console.error("Backend Error:", err.response);
    console.error("Backend Data:", err.response?.data);
    console.error("Backend Status:", err.response?.status);

    const errorData = err.response?.data;

    // Handle structured validation errors
    if (errorData?.errors) {
      const messages = Object.values(errorData.errors).flat().join("\n");
      alert("Error:\n" + messages);
    } else if (errorData?.title) {
      alert("Error: " + errorData.title);
    } else if (err.response?.status) {
      alert("Error: Status " + err.response.status);
    } else {
      alert("Unknown error occurred");
    }
  }
};


  return (
    <div className="app-container">
      <form className="login-form" onSubmit={handleSubmit}>
        
        <h2>{action}</h2>

        {/* Show Name ONLY on Register */}
        {action === "Register" && (
          <input
            type="text"
            placeholder="Enter your full name"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        )}

        <input
          type="text"
          placeholder="Enter username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          type="password"
          placeholder="Enter password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        {/* Login/Register Toggle Buttons */}
        <div className="submit-container">
          <button
            type="button"
            className={action === "Login" ? "submit gray" : "submit"}
            onClick={() => setAction("Register")}
          >
            Register
          </button>

          <button
            type="button"
            className={action === "Register" ? "submit gray" : "submit"}
            onClick={() => setAction("Login")}
          >
            Login
          </button>
        </div>

        {/* REAL SUBMIT BUTTON */}
        <button style={{
          color:'magenta',
          backgroundColor:'lavender',
          border:'none',
          padding:'auto'
        }} type="submit" className="submit main-submit">
          submit
        </button>

        <button
          type="button"
          className="admin-btn"
          onClick={() => navigate('/adminlogin')}
        >
          Admin Login
        </button>

      </form>
    </div>
  );
};

export default Login;
