// src/Pages/User/ViewStatus.js
import React, { useEffect, useState } from "react";
import api from "../../axiosConfig";
import { useNavigate } from "react-router-dom";
import "./UserDashboard.css";
import { Link } from "react-router-dom";

const ViewStatus = () => {
  const navigate = useNavigate();
  const [feedbacks, setFeedbacks] = useState([]);
  const [user, setUser] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) navigate("/UserLogin");
    fetchFeedbacks();
  }, []);

  const fetchFeedbacks = async () => {
    try {
      const res = await api.get("Feedback/my-feedbacks");
      setFeedbacks(res.data);
    } catch (err) { console.error(err); }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };
  const users= localStorage.getItem("userName");


  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {users}</h2>
        <Link to="/SubmitFeedback" className="link-nav">Submit new feedback</Link>
        <Link to="/ViewStatus" className="link-nav">View Recents</Link>
        <Link to="/SubmittedFeedbacks" className="link-nav">Submitted Feedbacks</Link>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <div className="dashboard-content">
        <h3>Recent Feedbacks</h3>
        <div className="table-container">
          {feedbacks.length === 0 ? (
            <p>You havenâ€™t submitted any feedback yet.</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Mentor</th>
                  <th>Week</th>
                  <th>Submitted On</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
              {feedbacks
                .sort((a, b) => new Date(b.submittedOn) - new Date(a.submittedOn)) // newest first
                .slice(0, 5) // pick top 5 recent
                .map(f => (
                  <tr key={f.id}>
                    <td>{f.mentorName}</td>
                    <td>{f.week}</td>
                    <td>{f.submittedOn ? new Date(f.submittedOn).toLocaleDateString() : "N/A"}</td>
                    {/* <td>{f.status}</td> */}
                       <td className="status"
                        style={{
                        color:
                          f.status === "Pending"
                            ? "red"
                            : f.status === "Approved"
                            ? "green"
                            : "black",
                        fontWeight: "600"
                      }}
                    >
                      {f.status}
                    </td>
                  </tr>
              ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default ViewStatus;
