import React, { useState, useEffect } from "react";
import { Link, NavLink, Outlet, useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

function AdminDashboard() {
  const [open, setOpen] = useState(false);
  const navigate = useNavigate();
  const [adminName, setAdminName] = useState("");

  useEffect(() => {
    const storedName = localStorage.getItem("adminName");
    if (storedName) setAdminName(storedName);
  }, []);


  return (
    <div className="dashboard">
      {/* HEADER */}
      <div className="header">
       
        <div className="welcome-box">
          <span className="user-icon">ðŸ‘¤</span>
          <span>Welcome, {adminName}</span>
        </div>
      </div>

      {/* HAMBURGER */}
      <div
        className="hamburger"
        onClick={() => setOpen(!open)}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>

      {/* SIDEBAR */}
      <div className={open ? "sidebar open" : "sidebar"}>
        <h3>Admin Menu</h3>
        <NavLink to='/AllUsers' className="link">All Users</NavLink>
        <NavLink to="pending" className="link">Pending Approvals</NavLink>
        <NavLink to="approved" className="link">Approved Feedbacks</NavLink>
        <NavLink to="/AdminLogin" className="link">Logout</NavLink>
      </div>

      {/* OVERLAY */}
      {open && <div className="overlay" onClick={() => setOpen(false)}></div>}

      {/* MAIN CONTENT */}
      <div className={open ? "content sidebar-open" : "content"}>
        <div className="cards">
          <div className="card">
            <h3>All Users</h3>
            <Link ></Link>
            <p>View all registered users.</p>
          </div>
          <div className="card">
            <h3>Pending Approvals</h3>
            <p>Check feedbacks awaiting approval.</p>
          </div>
          <div className="card">
            <h3>Approved Feedbacks</h3>
            <p>See all approved feedbacks.</p>
          </div>
        </div>
        <Outlet />
      </div>
    </div>
  );
}

export default AdminDashboard;
