// src/Pages/User/SubmitFeedback.js
import React, { useState } from "react";

export default function SubmitFeedback() {
  const [mentor, setMentor] = useState("");
  const [week, setWeek] = useState("");
  const [hours, setHours] = useState("");
  const [queries, setQueries] = useState("");
  const [openQueries, setOpenQueries] = useState("");
  const [linesOfCode, setLinesOfCode] = useState("");
  const [rating, setRating] = useState(1);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const feedbackData = {
      mentorName: mentor,
      week,
      queries,
      openQueries,
      description: linesOfCode,
      rating,
      status: "Pending"
    };

    const userId = localStorage.getItem("userId"); // or wherever you store user info
    feedbackData.userId = userId;

    try {
      const res = await fetch("https://localhost:7079/api/feedback", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(feedbackData)
      });
      if (!res.ok) throw new Error("Failed to submit feedback");
      alert("Feedback submitted successfully!");
    } catch (err) {
      console.log(err);
      alert("Error submitting feedback");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Mentor Name" value={mentor} onChange={e=>setMentor(e.target.value)} required />
      <input type="text" placeholder="Week" value={week} onChange={e=>setWeek(e.target.value)} required />
      <input type="number" placeholder="Hours Spent" value={hours} onChange={e=>setHours(e.target.value)} required />
      <input type="text" placeholder="Queries" value={queries} onChange={e=>setQueries(e.target.value)} />
      <input type="text" placeholder="Open Queries" value={openQueries} onChange={e=>setOpenQueries(e.target.value)} />
      <input type="text" placeholder="Lines of Code" value={linesOfCode} onChange={e=>setLinesOfCode(e.target.value)} />
      <label>Rating:</label>
      <select value={rating} onChange={e=>setRating(e.target.value)}>
        <option value={1}>⭐</option>
        <option value={2}>⭐⭐</option>
        <option value={3}>⭐⭐⭐</option>
        <option value={4}>⭐⭐⭐⭐</option>
        <option value={5}>⭐⭐⭐⭐⭐</option>
      </select>
      <button type="submit">Submit Feedback</button>
    </form>
  );
}
