import React, { useEffect, useState } from "react";
import api from "../../axiosConfig";
import { useNavigate } from "react-router-dom";
import "./SubmittedFeedbacks.css"; // optional separate CSS or reuse dashboard CSS

const SubmittedFeedbacks = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [feedbacks, setFeedbacks] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/UserLogin");
      return;
    }

    const payload = JSON.parse(atob(token.split(".")[1]));
    setUsername(payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]);

    fetchFeedbacks();
  }, []);

  const fetchFeedbacks = async () => {
    try {
      const res = await api.get("Feedback/my-feedbacks");
      console.log(res.data);
      setFeedbacks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };
   //fetch userName
  const user = localStorage.getItem("userName");

  return (
    <div className="submitted-container">
      <header className="submitted-header">
        <h2>Welcome, {username}</h2>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <main className="submitted-content">
        <h3>All Submitted Feedbacks</h3>
        <table>
          <thead>
            <tr>
              <th>Mentor</th>
              <th>Week</th>
              <th>Submitted On</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {feedbacks.length > 0 ? feedbacks.map(f => (
              <tr key={f.id}>
                <td>{f.mentorName}</td>
                <td>{f.week}</td>
                <td>{f.submittedOn ? new Date(f.submittedOn).toLocaleDateString() : "N/A"}</td>
                <td>{f.status}</td>
              </tr>
            )) : (
              <tr>
                <td colSpan="4">No feedbacks submitted yet.</td>
              </tr>
            )}
          </tbody>
        </table>
      </main>
    </div>
  );
};

export default SubmittedFeedbacks;
