import React, { useEffect, useState } from "react";
import api from "../../axiosConfig";
import { useNavigate } from "react-router-dom";
import "./UserDashboard.css";
import DashboardHeader from "./DashboardHeader";
import FeedbackTable from "./FeedbackTable";  // ADDED

const ViewStatus = () => {
  const navigate = useNavigate();
  const [feedbacks, setFeedbacks] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) navigate("/UserLogin");
    fetchFeedbacks();
  }, []);

  const fetchFeedbacks = async () => {
    try {
      const res = await api.get("Feedback/my-feedbacks");
      setFeedbacks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };

  const users = localStorage.getItem("userName");

  return (
    <div className="dashboard-container">
      <DashboardHeader username={users} onLogout={handleLogout} />

      <div className="dashboard-content">
        <h3>Recent Feedbacks</h3>

        <div className="table-container">
          {feedbacks.length === 0 ? (
            <p>You havenâ€™t submitted any feedback yet.</p>
          ) : (
            <FeedbackTable
              feedbacks={feedbacks
                .sort((a, b) => new Date(b.submittedOn) - new Date(a.submittedOn))
                .slice(0, 5)}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default ViewStatus;
