import React, { useState } from "react";
import api from "../../axiosConfig";
import "./UserLogin.css"; 
import { useNavigate } from "react-router-dom";

const Register = () => {
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");
  const [isPasswordValid, setIsPasswordValid] = useState(true); // state for validation

  const validatePassword = (password) => {
    // Regex: Match any character (except newline) 8 or more times
    const passwordRegex = /^.{8,}$/;
    return passwordRegex.test(password);
  };

  // ðŸ“ HANDLER FOR PASSWORD INPUT
  const handlePasswordChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);
    
    // Clear any previous general error message
    setMsg(""); 

    // Validate the password in real-time
    if (newPassword.length > 0) {
        setIsPasswordValid(validatePassword(newPassword));
    } else {
        // If empty, reset validation state (required attribute handles empty submission)
        setIsPasswordValid(true);
    }
  };


  const handleRegister = async (e) => {
    e.preventDefault();

    // ðŸ›‘ CLIENT-SIDE VALIDATION CHECK BEFORE SUBMISSION
    if (!validatePassword(password)) {
        setMsg("Password must be at least 8 characters long.");
        setIsPasswordValid(false);
        return; // Stop form submission
    }

    // Reset validation state and proceed with API call
    setMsg("");
    setIsPasswordValid(true); 

    try {
      await api.post("UserLogin/register", {
        name,
        userName: username,
        password,
        Role: "User", 
      });
      setMsg("Registration successful! Redirecting to login...");
      setTimeout(() => navigate("/UserLogin"), 2000); 
    } catch (err) {
      console.error(err.response?.data || err.message);
      setMsg("Registration failed- Username already exists or server error.");
    }
  };

  // Helper function to display detailed password error message
  const getPasswordValidationMessage = () => {
    if (password.length > 0 && !isPasswordValid) {
      if (password.length < 8) return 'Password must be at least 8 characters long.';
    }
    return '';
  };

  return (
    <div className="login-container">
      <form className="login-box" onSubmit={handleRegister}>
        <h2 className="login-title">Rate-Right</h2>
        <h2 className="login-title">Register</h2>

        <input
          type="text"
          placeholder="Full Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={handlePasswordChange} // â¬…ï¸ Using new handler for validation
          required
          // Optional visual feedback for invalid password
          style={{ 
              borderColor: (password.length > 0 && !isPasswordValid) ? 'red' : '' 
          }}
        />

        {/* ðŸ’¡ Display password requirement feedback */}
        {getPasswordValidationMessage() && (
            <p className="info-msg" style={{ color: 'orange' }}>
                {getPasswordValidationMessage()}
            </p>
        )}
        
        {/* Display success/error message */}
        {msg && <p className="info-msg">{msg}</p>}

        {/* Disable button if password is not valid and is not empty */}
        <button 
            type="submit" 
            className="primary-btn" 
            disabled={password.length > 0 && !isPasswordValid}
        >
            Register
        </button>

        <button
          type="button"
          className="secondary-btn"
          onClick={() => navigate("/UserLogin")}
        >
          Back to Login
        </button>
      </form>
    </div>
  );
};

export default Register;
