import React, { useState } from "react";
import api from "../../axiosConfig";
import { useNavigate } from "react-router-dom";
import "./UserLogin.css";

const Login = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isValid, setIsValid] = useState(true); // Tracks password validity
  const [msg, setMsg] = useState("");

  // REGEX FOR PASSWORD VALIDATION (MINIMUM LENGTH 8 ONLY)
  const validatePassword = (password) => {
    // Explanation of the Regex:
    // ^      - Start of the string
    // .{8,}  - Match any character (except newline) exactly 8 or more times
    // $      - End of the string
    const passwordRegex = /^.{8,}$/;
    return passwordRegex.test(password);
  };

  // HANDLER FOR PASSWORD CHANGE
  const handlePasswordChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);

    // Clear any previous general error message when password starts changing
    setMsg(""); 

    // Check password requirements
    if (newPassword.length > 0) {
        const isPassValid = validatePassword(newPassword);
        setIsValid(isPassValid);
    } else {
        // If the field is empty, consider it temporarily valid but required by HTML 'required' attr
        setIsValid(true); 
    }
  };


  const handleLogin = async (e) => {
    e.preventDefault();

    // ðŸ’¡ STEP 1: CLIENT-SIDE PASSWORD VALIDATION CHECK
    if (!validatePassword(password)) {
        setMsg("Password must be at least 8 characters long.");
        setIsValid(false);
        return; // STOP submission if validation fails
    }
    
    // Reset message and validation state if validation passes
    setMsg(""); 
    setIsValid(true);

    // ðŸ’¡ STEP 2: PROCEED WITH API CALL
    try {
      const res = await api.post("UserLogin/login", {
        UserName: username,
        Password: password,
      });

      const user = res.data;
      if (!user) {
        setMsg("Invalid login");
        return;
      }

      localStorage.setItem("token", user.token);
      localStorage.setItem("userName", user.userName);
      
      const userRole = user.role;
      if (userRole === "Admin") navigate("/AdminDashboard");
      else navigate("/UserDashboard");
    } catch (err) {
      console.error(err.response?.data || err.message);
      setMsg("Invalid username or password");
    }
  };

  // Helper function to display detailed password error message
  const getPasswordValidationMessage = () => {
    if (password.length > 0 && !isValid) {
      // Only check length since other rules were removed
      if (password.length < 8) return 'Password must be at least 8 characters long.';
    }
    return '';
  };


  return (
    <div className="login-container">
      <form className="login-box" onSubmit={handleLogin}>
        <h2 className="login-title">Rate-Right</h2>
        <h2 className="login-title">Login</h2>
        <input
          type="text"
          placeholder="Enter Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={handlePasswordChange} // â¬…ï¸ Changed to new handler
          required
          // Add visual styling (optional)
          style={{ 
              borderColor: (password.length > 0 && !isValid) ? 'red' : '' 
          }}
        />
        
        {/* ðŸ’¡ Display detailed password requirement feedback */}
        {getPasswordValidationMessage() && (
            <p className="error-msg" style={{ color: 'orange' }}>
                {getPasswordValidationMessage()}
            </p>
        )}

        {/* ðŸ’¡ Display API/General error message */}
        {msg && <p className="error-msg">{msg}</p>}

        <button type="submit" className="primary-btn">Login</button>
        <button
          type="button"
          className="secondary-btn"
          onClick={() => navigate("/UserRegister")}
        >
          Register
        </button>
      </form>
    </div>
  );
};
export default Login;
