import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css"; // reuse your existing styles

const UserLogin = () => {
  const navigate = useNavigate();

  const [action, setAction] = useState("Login"); // "Login" or "Register"
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState(""); // only for register

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (action === "Login") {
        const response = await axios.post(
          "https://localhost:7079/api/UserLogin/login",
          { username, password }
        );
        localStorage.setItem("token", response.data.token);
        navigate("/userdashboard"); // redirect after login
      }
    } catch (err) {
      console.error(err);
      alert(err.response?.data || "Something went wrong.");
    }
  };

  return (
    <div className="app-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>{action}</h2>

        
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        )}

        <input
          type="text"
          placeholder="Enter username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          type="password"
          placeholder="Enter password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <div className="submit-container">
          <button
            type="button"
            className={action === "Login" ? "submit gray" : "submit"}
            onClick={() => setAction("Register")}
          >
            Register
          </button>
          <button
            type="button"
            className={action === "Register" ? "submit gray" : "submit"}
            onClick={() => setAction("Login")}
          >
            Login
          </button>
        </div>

        <button type="submit" className="admin-btn">
          {action === "Login" ? "Login" : "Register"}
        </button>
      </form>
    </div>
  );
};

export default UserLogin;
