import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../axiosConfig";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [adminName, setAdminName] = useState("");

  // Reports data
  const [totalUsers, setTotalUsers] = useState(0);
  const [totalFeedbacks, setTotalFeedbacks] = useState(0);
  const [pendingFeedbacks, setPendingFeedbacks] = useState(0);
  const [averageRating, setAverageRating] = useState(0);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/UserLogin");
      return;
    }

    const payload = JSON.parse(atob(token.split(".")[1]));
    setAdminName(payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]);

    fetchReports();
  }, []);

  const fetchReports = async () => {
    try {
      const usersRes = await api.get("Admin/Getallusers");
      const feedbacksRes = await api.get("Feedback/all-feedbacks");

      const users = usersRes.data.data || usersRes.data; // handle possible wrapper
      const feedbacks = feedbacksRes.data || [];

      setTotalUsers(users.length);
      setTotalFeedbacks(feedbacks.length);
      setPendingFeedbacks(feedbacks.filter(f => f.status === "Pending").length);

      const avgRating =
        feedbacks.length > 0
          ? (feedbacks.reduce((sum, f) => sum + f.rating, 0) / feedbacks.length).toFixed(1)
          : 0;
      setAverageRating(avgRating);
    } catch (err) {
      console.error("Error fetching reports:", err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {adminName}</h2>
        <nav>
          <button onClick={() => navigate("/AllUsers")}>All Users</button>
          <button onClick={() => navigate("/PendingFeedbacks")}>Approve Pending Feedbacks</button>
          <button onClick={() => navigate("/ApproveFeedbackbyId")}>Approved Feedbacks by Id</button>
        </nav>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <main className="dashboard-content">
        <h3>Reports</h3>
        <div className="reports-grid">
          <div className="report-card">
            <h4>Total Users</h4>
            <p>{totalUsers}</p>
          </div>
          <div className="report-card">
            <h4>Total Feedbacks</h4>
            <p>{totalFeedbacks}</p>
          </div>
          <div className="report-card">
            <h4>Pending Feedbacks</h4>
            <p>{pendingFeedbacks}</p>
          </div>
          <div className="report-card">
            <h4>Average Rating</h4>
            <p>{averageRating}</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
