import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
// NOTE: The import statement for the logo is REMOVED because 
// the image is now in the public folder.

const SplashScreen = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // Timer controls the duration of the splash screen
    const timer = setTimeout(() => {
      // Navigate to the login page after 2.5 seconds
      navigate("/UserLogin");
    }, 5500);

    // Cleanup function
    return () => clearTimeout(timer);
  }, [navigate]);

  return (
    <div
      className="splash-container"
      style={{
        height: "100vh",
        width: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: " rgba(246, 247, 250, 1) ",
        fontFamily: "Arial",
      }}
    >
      <div
        className="splash-content"
        style={{ textAlign: "center" }}
      >
        <img
          src="/Logo2.png" 
          alt="Rate Right Logo" 
          style={{
            width: '250px', 
            height: 'auto',
            marginBottom: '20px', 
          }}
        />
        
        <h1
          className="splash-title"
          style={{
            fontSize: "28px",
            fontWeight: "bold",
            color: "purple",
            marginBottom: "10px",
          }}
        >
          Welcome to RATE RIGHT ðŸŒŸ
        </h1>

        <p
          className="splash-subtitle"
          style={{
            fontSize: "16px",
            color: "purple",
          }}
        >
          Loading...
        </p>
      </div>
    </div>
  );
};

export default SplashScreen;
