import React from "react";
import { useNavigate } from "react-router-dom";
import "./Header.css";

const Header = ({ username, role }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/Login");
  };

  return (
    <header className="app-header">
      <div className="logo">MyApp</div>

      <nav className="nav-buttons">
        {role === "User" && (
          <>
            <button onClick={() => navigate("/UserDashboard/submit")}>Submit New Feedback</button>
            <button onClick={() => navigate("/UserDashboard/recent")}>View Status</button>
            <button onClick={() => navigate("/UserDashboard/submitted")}>Submitted Feedbacks</button>
          </>
        )}
        {role === "Admin" && (
          <>
            <button onClick={() => navigate("/AdminDashboard/all")}>All Feedbacks</button>
            <button onClick={() => navigate("/AdminDashboard/pending")}>Pending Approvals</button>
            <button onClick={() => navigate("/AdminDashboard/approved")}>Approved Feedbacks</button>
          </>
        )}
      </nav>

      <div className="user-info">
        <span>Welcome, {username}</span>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </div>
    </header>
  );
};

export default Header;
