// src/Pages/User/SubmitFeedback.js
import React, { useState } from "react";
import "./SubmitFeedback.css"; // make sure you import your CSS file

export default function SubmitFeedback() {
  const [mentor, setMentor] = useState("");
  const [week, setWeek] = useState("");
  const [hours, setHours] = useState("");
  const [queries, setQueries] = useState("");
  const [openQueries, setOpenQueries] = useState("");
  const [linesOfCode, setLinesOfCode] = useState("");
  const [rating, setRating] = useState(1);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const feedbackData = {
      mentorName: mentor,
      week,
      hours,
      queries,
      openQueries,
      description: linesOfCode,
      rating,
      status: "Pending",
      userId: localStorage.getItem("userId")
    };

    try {
      const res = await fetch("https://localhost:7079/api/feedback", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(feedbackData)
      });

      if (!res.ok) throw new Error("Failed to submit feedback");
      alert("Feedback submitted successfully!");
    } catch (err) {
      alert("Error submitting feedback");
      console.log(err);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      
      <label>Mentor Name</label>
      <input
        type="text"
        value={mentor}
        onChange={(e) => setMentor(e.target.value)}
        required
      />

      <label>Week</label>
      <input
        type="text"
        value={week}
        onChange={(e) => setWeek(e.target.value)}
        required
      />

      <label>Hours Spent</label>
      <input
        type="number"
        value={hours}
        onChange={(e) => setHours(e.target.value)}
        required
      />

      <label>Queries Answered</label>
      <input
        type="text"
        value={queries}
        onChange={(e) => setQueries(e.target.value)}
      />

      <label>If you have open queries, please describe them here (Type NA if no queries for this week)</label>
      <input
        type="text"
        value={openQueries}
        onChange={(e) => setOpenQueries(e.target.value)}
      />

      <label>How many lines of code did you write this week?</label>
      <input
        type="text"
        value={linesOfCode}
        onChange={(e) => setLinesOfCode(e.target.value)}
      />

      <label>Overall, how would you rate your mentoring experience this week?</label>
      <select value={rating} onChange={(e) => setRating(e.target.value)}>
        <option value={1}>⭐</option>
        <option value={2}>⭐⭐</option>
        <option value={3}>⭐⭐⭐</option>
        <option value={4}>⭐⭐⭐⭐</option>
        <option value={5}>⭐⭐⭐⭐⭐</option>
      </select>

      <button type="submit">Submit Feedback</button>
    </form>
  );
}
