import React, { useState, useEffect } from "react";
import { NavLink, Outlet } from "react-router-dom";
import "./UserDashboard.css";

function UserDashboard() {
  const [open, setOpen] = useState(false);
  const [username, setUsername] = useState("");

  useEffect(() => {
    const name = localStorage.getItem("username");
    if (name) setUsername(name);
  }, []);

  return (
    <div className="dashboard">

      <div className="header">

        <div className="hamburger" onClick={() => setOpen(!open)}>
          <div></div>
          <div></div>
          <div></div>
        </div>

        <h2>User Dashboard</h2>

        <div className="user-right">
          <span className="user-icon">ðŸ‘¤</span>
          <p>Welcome, {username}</p>
        </div>
      </div>

      <div className={open ? "sidebar open" : "sidebar"}>
        <h3>User Menu</h3>

        <ul>
          <li><NavLink to="home">Home</NavLink></li>
          <li><NavLink to="submit">Submit Feedback</NavLink></li>
          <li><NavLink to="my-feedback">My Feedback</NavLink></li>
          <li><NavLink to="/UserLogin">Logout</NavLink></li>
        </ul>
      </div>

      {/* MAIN AREA */}
      <div className="main">
        <div className="cards">

          <div className="card">
            <h4>Recent Feedback Status</h4>
            <p>Check the latest update on your feedback.</p>
          </div>

          <div className="card">
            <h4>Submit New Feedback</h4>
            <p>Create and send new feedback.</p>
          </div>

          <div className="card">
            <h4>My Feedback History</h4>
            <p>View all your previous submissions.</p>
          </div>

        </div>

        <Outlet />
      </div>
    </div>
  );
}

export default UserDashboard;
