import React from 'react'
import { useState } from "react";
import axios from 'axios';


const UserLogin = () => {
    const[Username,setUsername]=useState('')
const[Password,setPassword]=useState('')

const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    const response = await axios.post("https://localhost:7079/api/UserLogin/login", {
        Username,
        Password,
      });
      localStorage.setItem("token", response.data.token);
      navigate("/userdashboard");
    } catch (err) {
    console.error("Backend Error:", err.response);
    console.error("Backend Data:", err.response?.data);
    console.error("Backend Status:", err.response?.status);
    }}
  return (
    <div>
        <form>
            <h2>LOGIN</h2>
            <input
            type="username"
            placeholder="enter username"
            onChange={(e)=>setUsername(e.target.value)}

            />
            <input
            type="password"
            placeholder="enter password"
            onChange={(e)=>setPassword(e.target.value)}
            />
            <button type='submit'>Login</button>
        </form>
    </div>
  )
}

export default UserLogin
