import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [adminName, setAdminName] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/AdminLogin");
      return;
    }

    const payload = JSON.parse(atob(token.split(".")[1]));
    setAdminName(payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/AdminLogin");
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {adminName}</h2>
        <nav>
          <button onClick={() => navigate("/AllUsers")}>All Users</button>
          <button onClick={() => navigate("/PendingFeedbacks")}>Pending Approvals</button>
          <button onClick={() => navigate("/ApproveFeedback")}>Approved Feedbacks</button>
        </nav>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <main className="dashboard-content">
        <p>Select an option above to manage users or feedbacks.</p>
      </main>
    </div>
  );
};

export default AdminDashboard;
