import React from 'react'
import axios from 'axios'
import { useState} from 'react'
import { useNavigate } from 'react-router-dom'
import './Login.css';

const Login = () => {
  const navigate=useNavigate();
  const [username,setUsername]=useState('')
  const[password,setPassword]=useState('')
  const[action,setAction]=useState('Login')

  const handlelogin= async (e)=>{
    e.preventDefault();
    try{
      const response= await axios.post("https://localhost:7079/api/Login/login" ,{
        username,
        password
      });
      localStorage.setItem("token",response.data.token)
      navigate("/userdashboard")
    } catch (err) {
      alert("Invalid login");
    }
  }
  return (
  <div className="app-container">
  <form className="login-form" onSubmit={handlelogin}>
    <h2>{action}</h2>
    {action==="Login"?<div></div>:<div className='input'>
                <input type="text" 
                placeholder='Enter your name'
                />
    </div >}
    <input
      type="text"
      placeholder="Enter username"
      value={username}
      onChange={(e) => setUsername(e.target.value)}
    />
    <input
      type="password"
      placeholder="Enter password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    {action==="Signup"?<div></div>:<div className='forgot-password'>Lost password?<span>click here</span></div>}
     <div className="button-row">
      <button type="submit">Login</button>
      <button type="button">Register</button>
    </div>
     <div className='submit-container'>
            <div className={action==="Login"?"submit gray":"submit"}onClick={()=>{setAction("Signup")}}>signup</div>
             <div className={action==="Signup"?"submit gray":"submit"}onClick={()=>{setAction("Login")}}>Login</div>
        </div>


    <button type="button" className="admin-btn">Admin Login</button>
  </form>
</div>
  )
}

export default Login
