// src/Pages/User/ViewStatus.js
import React, { useEffect, useState } from "react";
import api from "../../axiosConfig";
import "./ViewStatus.css"; // separate CSS for this page
import { useNavigate } from "react-router-dom";

const ViewStatus = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [feedbacks, setFeedbacks] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/Login");
      return;
    }

    const payload = JSON.parse(atob(token.split(".")[1]));
    setUsername(payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]);

    fetchFeedbacks();
  }, []);

  const fetchFeedbacks = async () => {
    try {
      const res = await api.get("Feedback/my-feedbacks");
      setFeedbacks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/Login");
  };

  return (
    <div className="viewstatus-container">
      <header className="viewstatus-header">
        <h2>Welcome, {username}</h2>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <main className="viewstatus-content">
        <h3>Recent Feedback Status</h3>
        <table>
          <thead>
            <tr>
              <th>Mentor</th>
              <th>Week</th>
              <th>Submitted On</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {feedbacks.slice(-5).reverse().map(f => (
              <tr key={f.id}>
                <td>{f.mentorName}</td>
                <td>{f.week}</td>
                <td>{f.createdAt ? new Date(f.createdAt).toLocaleDateString() : "N/A"}</td>
                <td>{f.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {feedbacks.length === 0 && <p>No feedbacks submitted yet.</p>}
      </main>
    </div>
  );
};

export default ViewStatus;
