import React, { useState, useEffect } from "react";
import { NavLink, Outlet } from "react-router-dom";
import "./UserDashboard.css";

function UserDashboard() {
  const [open, setOpen] = useState(false); // sidebar closed by default
  const [username, setUsername] = useState("");

  useEffect(() => {
    const u = localStorage.getItem("username");
    if (u) setUsername(u);
  }, []);

  return (
    <div className="dashboard">
      {/* HEADER */}
      <div className="header">
        <div className="hamburger" onClick={() => setOpen(!open)}>
          <span></span>
          <span></span>
          <span></span>
        </div>
        <h2>User Dashboard</h2>
        <div className="welcome-box">
          <span className="user-icon">üë§</span>
          <span>Welcome, {username}</span>
        </div>
      </div>

      {/* SIDEBAR */}
      <div className={open ? "sidebar open" : "sidebar"}>
        <h3>User Menu</h3>
        <NavLink to="home" className="link">üè† Home</NavLink>
        <NavLink to="submit" className="link">üìù Submit Feedback</NavLink>
        <NavLink to="my-feedback" className="link">üìÑ My Feedback</NavLink>
        <NavLink to="/UserLogin" className="link">üö™ Logout</NavLink>
      </div>

      {/* OVERLAY when sidebar open */}
      {open && <div className="overlay" onClick={() => setOpen(false)}></div>}

      {/* MAIN CONTENT */}
      <div className="content">
        <div className="cards">
          <div className="card">
            <h3>Recent Feedback</h3>
            <p>View your latest feedback status.</p>
          </div>
          <div className="card">
            <h3>New Feedback</h3>
            <p>Submit new feedback easily.</p>
          </div>
          <div className="card">
            <h3>My History</h3>
            <p>See all feedback submitted.</p>
          </div>
        </div>

        <Outlet />
      </div>
    </div>
  );
}

export default UserDashboard;
