// src/Pages/User/ViewStatus.js
import React, { useEffect, useState } from "react";
import api from "../../axiosConfig";
import "./ViewStatus.css";
import { useNavigate } from "react-router-dom";

const ViewStatus = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [feedbacks, setFeedbacks] = useState([]);

  // Run on page load
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/UserLogin"); // redirect if not logged in
      return;
    }

    // Decode token to get username
    const payload = JSON.parse(atob(token.split(".")[1]));
    setUsername(payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]);

    fetchFeedbacks();
  }, []);

  // Fetch feedbacks for this user
  const fetchFeedbacks = async () => {
    try {
      const res = await api.get("Feedback/my-feedbacks");
      setFeedbacks(res.data);
    } catch (err) {
      console.error("Error fetching feedbacks:", err);
    }
  };

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };
 //fetch userName
  const user = localStorage.getItem("userName");
  return (
    <div className="viewstatus-container">
      <header className="viewstatus-header">
        <h2>Welcome, {user}!</h2>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </header>

      <main className="viewstatus-content">
        <h3>Recent Feedbacks</h3>

        {feedbacks.length === 0 ? (
          <p>You havenâ€™t submitted any feedback yet.</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Mentor</th>
                <th>Week</th>
                <th>Submitted On</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {feedbacks.slice(-5).reverse().map(f => (
                <tr key={f.id}>
                  <td>{f.mentorName}</td>
                  <td>{f.week}</td>
                  <td>{f.submittedOn ? new Date(f.submittedOn).toLocaleDateString() : "N/A"}</td>
                  <td>{f.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </main>
    </div>
  );
};

export default ViewStatus;
