import React, { useState, useEffect } from "react";
import { NavLink, Outlet } from "react-router-dom";
import "./UserDashboard.css";

function UserDashboard() {
  const [open, setOpen] = useState(false);
  const [username, setUsername] = useState("");

  useEffect(() => {
    const u = localStorage.getItem("username");
    if (u) setUsername(u);
  }, []);

  return (
    <div className="dashboard">

      {/* Header */}
      <div className="header">

        {/* Hamburger always visible */}
        <div className="hamburger" onClick={() => setOpen(!open)}>
          <span></span>
          <span></span>
          <span></span>
        </div>

        <h2>User Dashboard</h2>

        <div className="welcome-box">
          <div className="user-circle">üë§</div>
          <p>Welcome, {username}</p>
        </div>
      </div>

      {/* Sidebar */}
      <div className={open ? "sidebar open" : "sidebar"}>
        <h3>User Menu</h3>

        <NavLink to="home">üè† Home</NavLink>
        <NavLink to="submit">üìù Submit Feedback</NavLink>
        <NavLink to="my-feedback">üìÑ My Feedback</NavLink>
        <NavLink to="/UserLogin">üö™ Logout</NavLink>
      </div>

      {/* Main Content */}
      <div className="content">

        {/* Dashboard Cards */}
        <div className="cards">
          <div className="card">
            <h3>Recent Feedback</h3>
            <p>View your latest feedback status.</p>
          </div>

          <div className="card">
            <h3>New Feedback</h3>
            <p>Submit new feedback easily.</p>
          </div>

          <div className="card">
            <h3>My History</h3>
            <p>See all feedback submitted.</p>
          </div>
        </div>

        <Outlet />
      </div>
    </div>
  );
}

export default UserDashboard;
