import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../axiosConfig";
import "./AdminDashboard.css";

const AdminDashboard = () => {
  const navigate = useNavigate();
  const [adminName, setAdminName] = useState("");
  const [reports, setReports] = useState({
    totalFeedbacks: 0,
    pending: 0,
    approved: 0,
    users: 0,
  });

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/UserLogin");
      return;
    }

    const payload = JSON.parse(atob(token.split(".")[1]));
    setAdminName(
      payload["http://schemas.microsoft.com/ws/2008/06/identity/claims/name"]
    );

    fetchReports();
  }, []);


  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/UserLogin");
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <h2>Welcome, {adminName}</h2>
        <nav className="dashboard-nav">
          <button onClick={() => navigate("/AllUsers")}>All Users</button>
          <button onClick={() => navigate("/PendingFeedbacks")}>
            Approve Pending Feedbacks
          </button>
          <button onClick={() => navigate("/ApproveFeedbackById")}>
            Approve by ID
          </button>
        </nav>
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </header>

      <main className="dashboard-content">
        <h3 className="reports-title">Reports</h3>
        <div className="reports-grid">
          <div className="report-card">
            <h4>Total Feedbacks</h4>
            <p>{reports.totalFeedbacks}</p>
          </div>
          <div className="report-card">
            <h4>Pending Feedbacks</h4>
            <p>{reports.pending}</p>
          </div>
          <div className="report-card">
            <h4>Approved Feedbacks</h4>
            <p>{reports.approved}</p>
          </div>
          <div className="report-card">
            <h4>Total Users</h4>
            <p>{reports.users}</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default AdminDashboard;
