import React, { useState } from "react";
import api from "../../axiosConfig";
import { useNavigate } from "react-router-dom";
import "./UserLogin.css";


const Login = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  //const [role, setRole] = useState("User"); // default role
  const [msg, setMsg] = useState("");

   const handleLogin = async (e) => {
  e.preventDefault();
  try {
    const res = await api.post("UserLogin/login", {
      UserName: username,
      Password: password,
    });

    const user = res.data;
    if (!user) {
      setMsg("Invalid login");
      return;
    }

    // Save token for protected routes
    localStorage.setItem("token", user.token);
localStorage.setItem("userName", user.User);
    // Navigate based on role directly from API response
    const userRole = user.role;  // use role from server response
    if (userRole === "Admin") navigate("/AdminDashboard");
    else navigate("/UserDashboard");
    
  } catch (err) {
    console.error(err.response?.data || err.message);
    setMsg("Invalid username or password");
  }
};

  return (
    <div className="login-container">
      <form className="login-box" onSubmit={handleLogin}>
        <h2 className="login-title">Login</h2>

        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        {/* <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="User">User</option>
          <option value="Admin">Admin</option>
        </select> */}

        {msg && <p className="error-msg">{msg}</p>}

       <button type="submit" className="primary-btn">Login</button>

        <button
          type="button"
          className="secondary-btn"
          onClick={() => navigate("/UserRegister")}
        >
          Register
        </button>
      </form>
    </div>
  );
};

export default Login;
