import React, { useState, useEffect } from "react";
import { NavLink, Outlet } from "react-router-dom";
import "./UserDashboard.css";

function UserDashboard() {
  const [open, setOpen] = useState(false);
  const [username, setUsername] = useState("");

  useEffect(() => {
    const u = localStorage.getItem("username");
    if (u) setUsername(u);
  }, []);

  return (
    <div className="dashboard">
      {/* HEADER */}
      <div className="header">
        <h2></h2>
        <div className="welcome-box">
          <span className="user-icon">üë§</span>
          <span>Welcome, {username}</span>
        </div>
      </div>

      {/* HAMBURGER */}
      <div
        className={`hamburger ${open ? "open" : ""}`}
        onClick={() => setOpen(!open)}
      >
        <span></span>
        <span></span>
        <span></span>
      </div>

      {/* SIDEBAR */}
      <div className={`sidebar ${open ? "open" : ""}`}>
        <h3>User Menu</h3>
        <NavLink to="home" className="link">üè† Home</NavLink>
        <NavLink to="submit" className="link">üìù Submit Feedback</NavLink>
        <NavLink to="my-feedback" className="link">üìÑ My Feedback</NavLink>
        <NavLink to="/UserLogin" className="link">üö™ Logout</NavLink>
      </div>

      {/* OVERLAY */}
      {open && <div className="overlay" onClick={() => setOpen(false)}></div>}

      {/* MAIN CONTENT */}
      <div className={open ? "content sidebar-open" : "content"}>
        <div className="cards">
          <div className="card">
            <h3>Recent Feedback</h3>
            <p>View your recefeedback status.</p>
          </div>
          <div className="card">
            <h3>New Feedback</h3>
            <p>Submit new feedback</p>
          </div>
          <div className="card">
            <h3>My History</h3>
            <p>See all the feedback submitted</p>
          </div>
        </div>
        <Outlet />
      </div>
    </div>
  );
}

export default UserDashboard;
