import React from 'react'
import axios from 'axios'
import { useState} from 'react'
import { useNavigate } from 'react-router-dom'
import './Login.css';

const Login = () => {
  const navigate=useNavigate();
  const [username,setUsername]=useState('')
  const[password,setPassword]=useState('')
  const[action,setAction]=useState('Login')

  const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    let response;

    if (action === "Login") {
      // LOGIN API
      response = await axios.post("https://localhost:7079/api/UserLogin/login", {
        username,
        password
      });

      localStorage.setItem("token", response.data.token);
      navigate("/userdashboard");
    } else {
      // REGISTER API
      response = await axios.post("https://localhost:7079/api/UserLogin/register", {
        username,
        password,
        
      });

      alert("Registered successfully! Please login now.");
      setAction("Login");
    }

  } catch (err) {
    alert("Something went wrong: " );
  }
};

  
  return (
  <div className="app-container">
  <form className="login-form" onSubmit={handleSubmit}>
    <h2>{action}</h2>
    {action==="Login"?<div></div>:<div className='input'>
                <input type="text" 
                placeholder='Enter your name'
                />
    </div >}
    <input
      type="text"
      placeholder="Enter username"
      value={username}
      onChange={(e) => setUsername(e.target.value)}
    />
    <input
      type="password"
      placeholder="Enter password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    {action==="Register"?<div></div>:<div className='forgot-password'>Lost password?<span>click here</span></div>}
     <div className="button-row">
    </div>
     <div className='submit-container'>
            <button className={action==="Login"?"submit gray":"submit"}onClick={()=>{setAction("Register")}}>Register</button>
             <button className={action==="Register"?"submit gray":"submit"}onClick={()=>{setAction("Login")}}>Login</button>
        </div>


    
<button
  type="button"
  className="admin-btn"
  onClick={() => navigate('/adminlogin')}
>
  Admin Login
</button>

  </form>
</div>
  )
}

export default Login
